<style>
  .main {
    background-color: var(--secondary-bg);
  }
  #skills {
    position: relative;
  }

  #skills::after {
    content: "";
    position: absolute;
    /* bottom: 10%; */

    right: -7.5rem;
    transform: rotate(4deg);
    bottom: 0;
    width: 50rem;
    height: 20rem;
    background-repeat: no-repeat;
    object-fit: cover;
    background-size: contain;
    background-position: bottom right;
    z-index: 0;
    background-image: url("../assets/icons/decoration-two.svg");
  }

  .list-skills {
    z-index: 2;
    position: relative;
    max-width: 70vw;
    margin: 5rem auto 3rem;
    justify-content: center;
    display: grid;
    grid-template-columns: repeat(3, max-content);
    gap: 2rem;
    font-family: var(--font-epi);
  }
  .list-skills > * {
    display: flex;
    align-items: center;
    gap: 10px;
    justify-self: start;
    background-color: var(--bg);
    padding: 2.5rem 2rem;
    width: 100%;
    max-width: 20rem;
    cursor: pointer;
    box-shadow: 1px 10px 20px rgba(32, 32, 32, 0.02);
    border-radius: 10px;
  }

  .list-skills p {
    align-content: end;
    column-gap: 1rem;
    border: 2px solid transparent;
  }

  .list-skills p:hover {
    border: 2px solid var(--secondary);
  }

  svg {
    filter: grayscale(1);
    width: 38px;
  }

  .list-skills p:hover svg {
    filter: grayscale(0);
  }

  @media screen and (max-width: 1055px) {
    .list-skills {
      margin-left: 1rem;
      margin-right: 1rem;
      max-width: 100vw;
      grid-template-columns: repeat(2, max-content);
    }
  }

  @media screen and (max-width: 700px) {
    .list-skills {
      grid-template-columns: 1fr;
      font-size: 1.5rem;
    }

    .list-skills > * {
      justify-content: center;
      justify-self: center;
    }
  }
</style>
